import React, { useEffect, useState } from "react";
import {
  createUser,
  getUserById,
  updateUser
} from "../../api/userApi";

const UserForm = ({ userId, onClose, onSuccess }) => {
  const [form, setForm] = useState({
    username: "",
    email: "",
    role: ""
  });

  const isEdit = Boolean(userId);

  // N·∫øu l√† s·ª≠a ‚Üí g·ªçi API l·∫•y th√¥ng tin user
  useEffect(() => {
    if (!userId) return;

    const fetchUser = async () => {
      try {
        const res = await getUserById(userId);
        setForm({
          username: res.data.username,
          email: res.data.email,
          role: res.data.role
        });
      } catch (error) {
        alert("Kh√¥ng l·∫•y ƒë∆∞·ª£c th√¥ng tin user");
      }
    };

    fetchUser();
  }, [userId]);

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isEdit) {
        await updateUser(userId, form);
      } else {
        await createUser(form);
      }

      onSuccess();
    } catch (error) {
      alert("L∆∞u user th·∫•t b·∫°i");
    }
  };

  return (
    <div style={modalStyle}>
      <h3>{isEdit ? "S·ª≠a User" : "Th√™m User"}</h3>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Username</label>
          <input
            name="username"
            value={form.username}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Email</label>
          <input
            name="email"
            value={form.email}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Role</label>
          <select
            name="role"
            value={form.role}
            onChange={handleChange}
            required
          >
            <option value="">-- Ch·ªçn role --</option>
            <option value="ADMIN">ADMIN</option>
            <option value="DOCTOR">DOCTOR</option>
            <option value="PATIENT">PATIENT</option>
          </select>
        </div>

        <div style={{ marginTop: 10 }}>
          <button type="submit">üíæ L∆∞u</button>
          <button
            type="button"
            onClick={onClose}
            style={{ marginLeft: 8 }}
          >
            ‚ùå Hu·ª∑
          </button>
        </div>
      </form>
    </div>
  );
};

const modalStyle = {
  position: "fixed",
  top: "20%",
  left: "35%",
  background: "#fff",
  padding: 20,
  border: "1px solid #ccc"
};

export default UserForm;
