import { Star, Calendar } from 'lucide-react';

const HomeDoctorSection = ({ doctors }) => {
  return (
    <section>
      <h2 className="text-2xl font-bold mb-6">
        Bác sĩ nổi bật
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {doctors.map(doc => (
          <div
            key={doc.id}
            className="bg-white p-6 rounded-2xl shadow-sm border border-slate-100
                       hover:shadow-lg transition-all text-center"
          >
            <div className="relative w-28 h-28 mx-auto mb-4">
              <img
                src={doc.avatarUrl}
                alt={doc.name}
                className="w-full h-full object-cover rounded-full
                           border-4 border-blue-50 shadow-inner"
              />
            </div>

            <h3 className="font-bold text-slate-800 mb-1">
              {doc.name}
            </h3>

            <p className="text-blue-600 text-sm font-semibold mb-3">
              {doc.specialtyName}
            </p>

            <div className="flex justify-center items-center gap-1 mb-4">
              <Star className="text-yellow-400 fill-yellow-400" size={16} />
              <span className="font-bold text-slate-700">
                {doc.rating}
              </span>
            </div>

            <button
              className="w-full py-2.5 bg-blue-600 text-white rounded-xl
                         font-bold hover:bg-blue-700 transition-all"
            >
              <Calendar size={18} className="inline mr-2" />
              Đặt lịch khám
            </button>
          </div>
        ))}
      </div>
    </section>
  );
};

export default HomeDoctorSection;
