import React, { useEffect, useState } from "react";
import {
  createUser,
  getRoles,
  getUserById,
  updateUser
} from "../../api/userApi";

const UserForm = ({ userId, onClose, onSuccess }) => {
  const [form, setForm] = useState({
    username: "",
    email: "",
    roleId: "",
    password: "" // üî• th√™m password
  });

  const [roles, setRoles] = useState([]);
  const isEdit = Boolean(userId);

  useEffect(() => {
    const fetchRoles = async () => {
      try {
        const res = await getRoles();
        setRoles(res.data || []);
      } catch {
        alert("Kh√¥ng l·∫•y ƒë∆∞·ª£c danh s√°ch role");
      }
    };

    const fetchUser = async () => {
      if (!userId) return;

      try {
        const res = await getUserById(userId);
        setForm(prev => ({
          ...prev,
          username: res.data.username,
          email: res.data.email,
          roleId: res.data?.role?.id || "",
          password: "" // ‚ùó kh√¥ng fill password khi edit
        }));
      } catch {
        alert("Kh√¥ng l·∫•y ƒë∆∞·ª£c th√¥ng tin user");
      }
    };

    fetchRoles();
    fetchUser();
  }, [userId]);

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      username: form.username,
      email: form.email,
      roleId: Number(form.roleId)
    };

    // üî• CH·ªà g·ª≠i password khi c√≥ nh·∫≠p
    if (form.password) {
      payload.password = form.password;
    }

    try {
      if (isEdit) {
        await updateUser(userId, payload);
      } else {
        await createUser(payload);
      }
      onSuccess();
    } catch {
      alert("L∆∞u user th·∫•t b·∫°i");
    }
  };

  return (
    <div style={modalStyle}>
      <h3>{isEdit ? "S·ª≠a User" : "Th√™m User"}</h3>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Username</label>
          <input
            name="username"
            value={form.username}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Email</label>
          <input
            name="email"
            value={form.email}
            onChange={handleChange}
            required
          />
        </div>

        {/* üî• PASSWORD */}
        <div>
          <label>
            M·∫≠t kh·∫©u {isEdit && <i>(ƒë·ªÉ tr·ªëng n·∫øu kh√¥ng ƒë·ªïi)</i>}
          </label>
          <input
            type="password"
            name="password"
            value={form.password}
            onChange={handleChange}
            required={!isEdit} // t·∫°o m·ªõi b·∫Øt bu·ªôc
          />
        </div>

        <div>
          <label>Role</label>
          <select
            name="roleId"
            value={form.roleId}
            onChange={handleChange}
            required
          >
            <option value="">-- Ch·ªçn role --</option>
            {roles.map(role => (
              <option key={role.id} value={role.id}>
                {role.name}
              </option>
            ))}
          </select>
        </div>

        <div style={{ marginTop: 10 }}>
          <button type="submit">üíæ L∆∞u</button>
          <button
            type="button"
            onClick={onClose}
            style={{ marginLeft: 8 }}
          >
            ‚ùå Hu·ª∑
          </button>
        </div>
      </form>
    </div>
  );
};

const modalStyle = {
  position: "fixed",
  top: "20%",
  left: "35%",
  background: "#fff",
  padding: 20,
  border: "1px solid #ccc"
};

export default UserForm;
